mapper.select:
select *
from emp
where name like concat('%', #{name}, '%')
  and gender = #{gender}
  and entrydate between #{begin} and #{end}
order by update_time desc

mapper.update:
update emp
username = #{username}, name = #{name}, gender = #{gender}, image = #{image}, job = #{job}, entrydate = #{entrydate}, dept_id = #{deptId}, update_time = #{updateTime}
where id = #{id}

